import React,{useState} from "react";
import {Typography} from '@material-ui/core'
import Popover from '@material-ui/core/Popover';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    popover: {
      pointerEvents: "none"
    },
    paper: {
      padding: " 1px 3px",
      marginTop:"2px"
    }
  })
);


const ImgWithPopover = ({emojiImage,popoverText}) =>{
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = useState<HTMLElement | null>(null);

  const handlePopoverOpen = (event: React.MouseEvent<HTMLElement, MouseEvent>) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);

  return(
    <>
    <img
    aria-owns={open ? "mouse-over-popover" : undefined}
    aria-haspopup="true"
    onMouseEnter={handlePopoverOpen}
    onMouseLeave={handlePopoverClose}
      src={emojiImage}
      style={{ height: "20px",cursor:"pointer"}}
      alt='emoji'
    />
    <Popover
    id="mouse-over-popover"
    className={classes.popover}
    classes={{
      paper: classes.paper
    }}
    open={open}
    anchorEl={anchorEl}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    onClose={handlePopoverClose}
    disableRestoreFocus>
    <Typography variant="caption" style={{pointerEvents:"none"}}>{popoverText}</Typography>
  </Popover>
    </>
  )

}

const PokemonEmojis = (emoji) => {

  switch (emoji.emoji) {
    case "like":
      return (
        <ImgWithPopover popoverText={"Like"} emojiImage={"data:image/png;base64,"} />
      );
    case "love":
      return (
        <ImgWithPopover popoverText={"Love"} emojiImage={"data:image/png;base64,"} />
      );
    case "haha":
      return (
        <ImgWithPopover popoverText={"Haha"} emojiImage={"data:image/png;base64,"} />
      );
    case "wow":
      return (
        <ImgWithPopover popoverText={"wow"} emojiImage={'data:image/png;base64,'} />
      );
    case "sad":
      return (
        <ImgWithPopover popoverText={"Sad"} emojiImage={"data:image/png;base64,"}/>
      );
    case "angry":
      return (
         <ImgWithPopover popoverText={"Angry"} emojiImage={"data:image/png;base64,"} />
        
      );
    default:
      return (
        <ImgWithPopover popoverText={"Like"} emojiImage={"data:image/png;base64,"} />
      );
  }
};

export default React.memo(PokemonEmojis);

